import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { fetchPostPhotos } from '../redux/postPhotosSlice'; // Подключаем thunk для загрузки фотографий

function PostComponent({ post }) {
    const dispatch = useDispatch();
    const photos = useSelector(state => state.postPhotos.photos); // Получаем фотографии из Redux store
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const getPhotos = async () => {
            try {
                const token = localStorage.getItem('Token');
                // Вызываем thunk для загрузки фотографий
                dispatch(fetchPostPhotos(post.id));
            } catch (error) {
                console.error('Error fetching photos:', error);
            }
        };
    
        getPhotos();

        // Убираем фотографии при размонтировании компонента
        return () => {
            dispatch(fetchPostPhotos(null)); // Очищаем фотографии
        };
    }, [dispatch, post.id]);

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === photos.length - 1 ? 0 : prevSlide + 1));
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 0 ? photos.length - 1 : prevSlide - 1));
    };

    return (
        <div className="post-card">
            <div className="carousel">
                {photos.length > 0 && (
                    <>
                        <img src={photos[currentSlide].image_path} alt={`Slide ${currentSlide + 1}`} />
                        <button className="prev" onClick={prevSlide}>Previous</button>
                        <button className="next" onClick={nextSlide}>Next</button>
                    </>
                )}
            </div>
            <h3>{post.name}</h3>
            <div className="description">
                {post.description}
            </div>
        </div>
    );
}

export default PostComponent;
