import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PostComponent({ post }) {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [photos, setPhotos] = useState([]);

    useEffect(() => {
        const getPhotos = async () => {
            try {
                const token = localStorage.getItem('Token');
                const response = await axios.get(`http://127.0.0.1:8000/api/image/getPhotos/${post.id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setPhotos(response.data);
            } catch (error) {
                if (error.response && error.response.status === 429) {
                    console.warn('Too many requests. Waiting before retrying...');
                    await new Promise(resolve => setTimeout(resolve, 5000)); // Ждем 5 секунд перед повторной попыткой
                    getPhotos(); // Повторяем запрос
                } else {
                    console.error('Error fetching photos:', error);
                }
            }
        };

        getPhotos();
    }, [post.id]);

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === photos.length - 1 ? 0 : prevSlide + 1));
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 0 ? photos.length - 1 : prevSlide - 1));
    };

    return (
        <div className="post-card">
            <h3>{post.name}</h3>
            <div className="carousel">
                {photos.map((photo, index) => (
                    <img key={index} src={photo.url} alt={`Slide ${index + 1}`} style={{ display: index === currentSlide ? 'block' : 'none' }} />
                ))}
                <button onClick={prevSlide}>Previous</button>
                <button onClick={nextSlide}>Next</button>
            </div>
        </div>
    );
}

export default PostComponent;
