// PostComponent.js
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchPostPhotos } from '../redux/PostPhotosSlice';

function PostComponent({ post }) {
  const dispatch = useDispatch();
  const photos = useSelector(state => state.postPhotos.photos);
  const loading = useSelector(state => state.postPhotos.loading);

  useEffect(() => {
    dispatch(fetchPostPhotos(post.id));
  }, [dispatch, post.id]);

  if (loading) {
    return <p>Loading photos...</p>;
  }

  return (
    <div className="post-card">
      <div className="carousel">
        {photos.map((photo, index) => (
          <img key={index} src={photo.image_path} alt={`Slide ${index + 1}`} />
        ))}
      </div>
      <h3>{post.name}</h3>
      <div className="description">
        {post.description}
      </div>
    </div>
  );
}

export default PostComponent;
