import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const loginUser = createAsyncThunk(
    'auth/loginUser',
    async ({ login, password }) => {
        const apiEndpointLogin = "http://127.0.0.1:8000/api/user/login";
        const formData = {
            "login": login,
            "password": password
        };

        try {
            const response = await axios.post(apiEndpointLogin, formData);
            const dataObject = response.data;
            localStorage.setItem('Token', dataObject.access_token);
            return dataObject;
        } catch (error) {
            throw new Error(error.response.data.message || error.message);
        }
    }
);

const AuthorizationSlice = createSlice({
    name: 'auth',
    initialState: {
        user: null,
        loading: false,
        error: null,
    },
    reducers: {
        clearError: (state) => {
            state.error = null;
        },
    },
    extraReducers: (builder) => {
        builder
            .addCase(loginUser.pending, (state) => {
                state.loading = true;
                state.error = null;
            })
            .addCase(loginUser.fulfilled, (state, action) => {
                state.loading = false;
                state.user = action.payload;
            })
            .addCase(loginUser.rejected, (state, action) => {
                state.loading = false;
                state.error = action.error.message;
            });
    },
});

export const { clearError } = AuthorizationSlice.actions;

export default AuthorizationSlice.reducer;
