import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Компонент для отображения одного поста
function PostComponent({ post }) {
    const [photos, setPhotos] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const pageSize = 10; // Размер страницы

    useEffect(() => {
        const getPhotos = async () => {
            try {
                const response = await axios.get(`http://127.0.0.1:8000/api/image/getPhotos/${post.id}?page=${currentPage}&pageSize=${pageSize}`);
                setPhotos(response.data.photos);
                setTotalPages(response.data.totalPages);
            } catch (error) {
                console.error('Error fetching photos:', error);
            }
        };

        getPhotos();
    }, [post.id, currentPage, pageSize]); // Вызывать эффект при изменении id поста, текущей страницы или размера страницы

    const nextSlide = () => {
        setCurrentPage((prevPage) => (prevPage === totalPages ? 1 : prevPage + 1));
    };

    const prevSlide = () => {
        setCurrentPage((prevPage) => (prevPage === 1 ? totalPages : prevPage - 1));
    };

    return (
        <div className="post-card">
            <h3>{post.name}</h3>
            {photos.length > 0 && (
                <div className="slider">
                    <button onClick={prevSlide}>Previous</button>
                    <img src={photos[currentSlide - 1]} alt={`Slide ${currentSlide}`} />
                    <button onClick={nextSlide}>Next</button>
                </div>
            )}
            {photos.length === 0 && <p>No photos available</p>}
        </div>
    );
}

export default PostComponent;
