import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { registerUser, clearError, uploadPhoto, clearRegistrationStatus, clearSuccess } from '../redux/RegistrationSlice'; // добавлено действие uploadPhoto
import '../css/registration.css';

function Registration() {
  const dispatch = useDispatch();
  const { loading, error, success } = useSelector((state) => state.registration);
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [birthDate, setBirthDate] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [photo, setPhoto] = useState(null);

  const handlePhotoChange = (event) => {
    const file = event.target.files[0];
    const fileName = file.name;
    localStorage.setItem('file_name', fileName);
    setPhoto(file);
    dispatch(uploadPhoto(file)); // диспетчируем действие для загрузки фотографии
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(clearError()); // Очищаем ошибку перед регистрацией
    dispatch(clearSuccess());
    dispatch(registerUser({ login, password, birthDate, name, email, photo }));
  };

  return (
    <div className="main">
      <div className="registration-form">
        <h2>Registration Form</h2>
        {loading && <div>Loading...</div>}
        {error && <div>Error: {error}</div>}
        {success && <div>Registration successful!</div>}
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <input type="text" value={login} onChange={(e) => setLogin(e.target.value)} placeholder="Login" required />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
          <input type="date" value={birthDate} onChange={(e) => setBirthDate(e.target.value)} placeholder="Birth Date" required />
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
          <input type="file" onChange={handlePhotoChange} accept="image/*" placeholder="Profile Photo" />
          <div className="buttons">
            <button type="submit">Register</button>
            <Link to="/authorization">
              <button style={{ backgroundColor: "orangered" }}>Authorize</button>
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Registration;
