import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Компонент для отображения одного поста
function PostComponent({ post }) {
    const [photos, setPhotos] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const [currentSlide, setCurrentSlide] = useState(1); // Добавляем currentSlide

    const pageSize = 10; // Размер страницы

    useEffect(() => {
        const getPhotos = async () => {
            try {
                const token = localStorage.getItem('Token');
                const response = await axios.get(`http://127.0.0.1:8000/api/image/getPhotos/${post.id}?page=${currentPage}&pageSize=${pageSize}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setPhotos(response.data.photos);
                setTotalPages(response.data.totalPages);
            } catch (error) {
                console.error('Error fetching photos:', error);
            }
        };

        getPhotos();
    }, [post.id, currentPage, pageSize]); // Вызывать эффект при изменении id поста, текущей страницы или размера страницы

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === totalPages ? 1 : prevSlide + 1));
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 1 ? totalPages : prevSlide - 1));
    };

    return (
        <div className="post-card">
            <h3>{post.name}</h3>
        </div>
    );
}

export default PostComponent;
