import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const registerUser = createAsyncThunk(
  'registration/registerUser',
  async ({ login, password, birthDate, name, email, photo }) => {
    const apiEndpointRegister = "http://127.0.0.1:8000/api/user/register";
    const formData = new FormData();
    formData.append('login', login);
    formData.append('password', password);
    formData.append('birth_date', birthDate);
    formData.append('name', name);
    formData.append('email', email);
    formData.append('photo', photo);
    const response = await axios.post(apiEndpointRegister, formData);
    return response.data;
  }
);

const RegistrationSlice = createSlice({
  name: 'registration',
  initialState: {
    loading: false,
    error: null,
    success: false,
  },
  reducers: {
    clearError: (state) => {
      state.error = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(registerUser.pending, (state) => {
        state.loading = true;
        state.error = null;
        state.success = false;
      })
      .addCase(registerUser.fulfilled, (state) => {
        state.loading = false;
        state.success = true;
      })
      .addCase(registerUser.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      });
  },
});

export const { clearError } = RegistrationSlice.actions;

export default registrationSlice.reducer;
