import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Компонент для отображения одного поста
function PostComponent({ post }) {
    const [currentSlide, setCurrentSlide] = useState(0);

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === post.photos.length - 1 ? 0 : prevSlide + 1));
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 0 ? post.photos.length - 1 : prevSlide - 1));
    };

    return (
        <div className="post-card">
            <h3>{post.description}</h3>
            {/* <div className="slider">
                <button onClick={prevSlide}>Previous</button>
                <img src={post.photos[currentSlide]} alt={`Slide ${currentSlide + 1}`} />
                <button onClick={nextSlide}>Next</button>
            </div> */}
        </div>
    );
}

export default PostComponent;
