import React, { useState } from 'react';
import '.css/App.css';

function Registration() {
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [birthDate, setBirthDate] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [photo, setPhoto] = useState(null);

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('login', login);
    formData.append('password', password);
    formData.append('birth_date', birthDate);
    formData.append('name', name);
    formData.append('email', email);
    formData.append('photo', photo);

    try {
      // Здесь можно вызвать функцию для отправки данных на сервер
      console.log(formData);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handlePhotoChange = (event) => {
    setPhoto(event.target.files[0]);
  };

  return (
    <div className="registration-form">
      <h2>Registration Form</h2>
      <form onSubmit={handleFormSubmit} encType="multipart/form-data">
        <input type="text" id="login" name="login" placeholder="Login" value={login} onChange={(e) => setLogin(e.target.value)} required />
        <input type="password" id="password" name="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <input type="date" id="birthDate" name="birthDate" placeholder="Birth Date" value={birthDate} onChange={(e) => setBirthDate(e.target.value)} required />
        <input type="text" id="name" name="name" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <input type="email" id="email" name="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="file" id="photo" name="photo" accept="image/*" onChange={handlePhotoChange} placeholder="Profile Photo" />
        <div className="buttons">
          <button type="submit">Register</button>
          <a href="auth.html">
            <button type="button" style={{ backgroundColor: 'orangered' }}>Authorize</button>
          </a>
        </div>
      </form>
    </div>
  );
}

export default Registration;
