import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PostComponent({ post }) {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [photos, setPhotos] = useState([]);

    useEffect(() => {
        const getPhotos = async () => {
            try {
                const token = localStorage.getItem('Token');
                console.log(post.id);
                const response = await axios.get(`http://127.0.0.1:8000/api/image/getPhotos/${post.id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                console.log(response.data, post.id)
                setPhotos(response.data);
            } catch (error) {
                console.error('Error fetching photos:', error);
            }
        };
    
        getPhotos();

        // Очищаем состояние photos при размонтировании компонента
        return () => {
            setPhotos([]);
        };
    }, [post.id]);

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === photos.length - 1 ? 0 : prevSlide + 1));
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 0 ? photos.length - 1 : prevSlide - 1));
    };

    return (
        <div className="post-card">
            <h3>{post.name}</h3>
            <div className="carousel">
                {photos.map((photo) => (
                    <img key={photo.id} src={photo.image} alt={`Slide ${photo.id}`} style={{ display: photo.id === currentSlide ? 'block' : 'none' }} />
                ))}
                <button onClick={prevSlide}>Previous</button>
                <button onClick={nextSlide}>Next</button>
            </div>
        </div>
    );
}

export default PostComponent;
