import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PostComponent({ post }) {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [photos, setPhotos] = useState([]);
    const [allPostsRendered, setAllPostsRendered] = useState(false);

    useEffect(() => {
        const getPhotos = async () => {
            try {
                const token = localStorage.getItem('Token');
                const response = await axios.get(`http://127.0.0.1:8000/api/image/getPhotos/${post.id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setPhotos(response.data);
            } catch (error) {
                console.error('Error fetching photos:', error);
            }
        };

        if (!allPostsRendered) {
            getPhotos();
        }

        return () => {
            setPhotos([]);
        };
    }, [post.id, allPostsRendered]);

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === photos.length - 1 ? 0 : prevSlide + 1));
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 0 ? photos.length - 1 : prevSlide - 1));
    };

    useEffect(() => {
        if (allPostsRendered && photos.length === 0) {
            // Если все посты отрисованы и для текущего поста нет изображений, устанавливаем флаг
            setAllPostsRendered(true);
        }
    }, [allPostsRendered, photos.length]);

    return (
        <div className="post-card">
            {photos.length > 0 && (
                <div className="carousel">
                    <img src={photos[currentSlide].image_path} alt={`Slide ${currentSlide + 1}`} />
                    <button className="prev" onClick={prevSlide}>Previous</button>
                    <button className="next" onClick={nextSlide}>Next</button>
                </div>
            )}
            <h3>{post.name}</h3>
            <div className="description">
                {post.description}
            </div>
        </div>
    );
}

export default PostComponent;
