import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../css/post.css';
import PostComponent from './PostComponent';

function PostFeed() {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);
    const observer = useRef();

    useEffect(() => {
        const getPosts = async () => {
            try {
                setLoading(true);
                const token = localStorage.getItem('Token');
                const response = await axios.get(`http://127.0.0.1:8000/api/post?page=${page}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setPosts(prevPosts => [...prevPosts, ...response.data.data]);
                setLoading(false);
                setPage(prevPage => prevPage + 1);
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        getPosts();
    }, [page]);

    useEffect(() => {
        const handleScroll = () => {
            if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight || loading) return;
            setLoading(true);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [loading]);

    useEffect(() => {
        observer.current = new IntersectionObserver(handleObserver, { threshold: 1 });
        if (observer.current) {
            observer.current.observe(document.querySelector('#end-of-list'));
        }
    }, []);

    const handleObserver = (entities) => {
        const target = entities[0];
        if (target.isIntersecting) {
            setPage(prevPage => prevPage + 1);
        }
    };

    return (
        <div className="posts">
            {posts.map((post) => (
                <PostComponent key={post.id} post={post} />
            ))}
            <div id="end-of-list" style={{ height: '10px', background: 'transparent' }}></div>
            {loading && <p>Loading...</p>}
        </div>
    );
}

export default PostFeed;
