import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { loginUser } from '../redux/AuthorizationSlice';
import { useNavigate } from 'react-router-dom';
import '../css/registration.css';

function Authorization() {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Отправляем данные на сервер для аутентификации
        dispatch(loginUser({ login, password }))
            .then(() => {
                // Перенаправляем на другую страницу при успешной аутентификации
                navigate("/main");
            })
            .catch(error => {
                console.error("Error:", error);
                // Обработка ошибки аутентификации, например, вывод сообщения об ошибке пользователю
            });
    };

    return (
        <div className="main">
            <div className="registration-form">
                <h2>Authorization Form</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        value={login}
                        onChange={(e) => setLogin(e.target.value)}
                        placeholder="Login"
                        required
                    />
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Password"
                        required
                    />
                    <button type="submit">Sign In</button>
                </form>
            </div>
        </div>
    );
}

export default Authorization;
