import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../css/post.css';
import PostComponent from './PostComponent';

function PostFeed() {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);
    const observer = useRef();

    useEffect(() => {
        const getPosts = async () => {
            try {
                setLoading(true);
                const token = localStorage.getItem('Token');
                const response = await axios.get(`http://127.0.0.1:8000/api/post?page=${currentPage}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setPosts(prevPosts => [...prevPosts, ...response.data.data]);
                setLoading(false);
                setCurrentPage(prevPage => prevPage + 1);
                setHasMore(response.data.next_page_url !== null);
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        if (hasMore && !loading) {
            getPosts();
        }
    }, [currentPage, hasMore, loading]);

    useEffect(() => {
        observer.current = new IntersectionObserver(handleObserver, { threshold: 0.5 });
        if (observer.current) {
            observer.current.observe(document.querySelector('#end-of-list'));
        }
    }, []);

    const handleObserver = (entities) => {
        const target = entities[0];
        if (target.isIntersecting && hasMore) {
            setCurrentPage(prevPage => prevPage + 1);
        }
    };

    return (
        <div className="posts">
            {posts.map((post, index) => (
                <PostComponent key={`${post.id}_${index}`} post={post} />
            ))}
            <div id="end-of-list" style={{ height: '10px', background: 'transparent' }}></div>
            {loading && <p>Loading...</p>}
        </div>
    );
}

export default PostFeed;
